%図6.3%clear all; close all;%パラメータの値を与えるK = 1; %K=1omegan = 1; %ω_n=1zeta1 = 0.1; %ζ=0.1zeta2 = 0.5; %ζ=0.5zeta3 = 0.9; %ζ=0.9%伝達関数の分子・分母多項式を与えるnum = [ 0 0 K*omegan^2 ]; %分子多項式den1 = [ 1 2*zeta1*omegan omegan^2 ]; %ζ=0.1の場合の分母多項式den2 = [ 1 2*zeta2*omegan omegan^2 ]; %ζ=0.5の場合の分母多項式den3 = [ 1 2*zeta3*omegan omegan^2 ]; %ζ=0.9の場合の分母多項式%伝達関数表現を与えるsys1 = tf( num, den1 ); %ζ=0.1の場合の伝達関数表現 sys2 = tf( num, den2 ); %ζ=0.5の場合の伝達関数表現  sys3 = tf( num, den3 ); %ζ=0.9の場合の伝達関数表現  %時間変数の定義t = 0:0.01:30; %0から30まで0.01刻み%インパルス応答の計算[ y1, t1 ] = impulse( sys1, t ); %ζ=0.1の場合のインパルス応答[ y2, t2 ] = impulse( sys2, t ); %ζ=0.5の場合のインパルス応答[ y3, t3 ] = impulse( sys3, t ); %ζ=0.9の場合のインパルス応答%図6.3のプロットplot(t1,y1,'-b',t2,y2,'-g',t3,y3,'-r'); %インパルス応答をプロットxlim([0,30]) %横軸（時間軸）の範囲の指定ylim([-1.0,1.0]) %縦軸の範囲の指定xticks([0 5 10 15 20 25 30]) %横軸の目盛りの値の設定yticks([-1.0 -0.5 0.0 0.5 1.0]) %縦軸の目盛りの値の設定grid; %罫線を表示xlabel('time  t [s]'); %横軸のラベル表示ylabel('y(t)') %縦軸のラベル表示legend('ζ = 0.1','ζ = 0.5','ζ = 0.9'); %凡例の表示