%図7.1%clear all; close all;%伝達関数の分子・分母多項式を与えるnum = [ 0 0 1 2 ]; %分子多項式den = [ 1 2 1 1 ]; %分母多項式%伝達関数表現を与えるsys = tf( num, den ); %伝達関数表現 %時間変数の定義t = 0:0.01:50; %0から50まで0.01刻み%ステップ応答の計算[ y, t ] = step( sys, t ); %ステップ応答%図7.1のプロットplot(t,y); %ステップ応答をプロットxlim([0,50]) %横軸（時間軸）の範囲の指定ylim([0.0 3.5]) %縦軸の範囲の指定xticks([0 10 20 30 40 50]) %横軸の目盛りの値の設定yticks([0.0 0.5 1.0 1.5 2.0 2.5 3.0 3.5]) %縦軸の目盛りの値の設定grid; %罫線を表示xlabel('time  t [s]'); %横軸のラベル表示ylabel('y(t)') %縦軸のラベル表示